                        </div>

                        <div className="result-item">
                          <span>Mortgage Payment:</span>
                          <span>
                            {formatCurrency(results.totalMonthlyPayment)}
                          </span>
                        </div>

                        {results.monthlyDebts > 0 && (
                          <div className="result-item">
                            <span>Other Debt Payments:</span>
                            <span>
                              {formatCurrency(results.monthlyDebts)}
                            </span>
                          </div>
                        )}

                        <div className="result-item total">
                          <span>Take-Home Remaining:</span>
                          <span>
                            {formatCurrency(results.monthlyTakeHome - results.totalMonthlyPayment - results.monthlyDebts)}
                          </span>
                        </div>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </div>
          ) : (
            <div className="no-results">
              <h3>Complete the form and calculate</h3>
              <p>
                Fill in your income and basic mortgage details to see what home price you can afford or 
                analyze a specific home price.
              </p>
            </div>
          )}

          {/* Affordability Guidelines */}
          <div className="guidelines">
            <h3>Affordability Guidelines: The 28/36 Rule</h3>
            <ul>
              <li>
                <strong>28% Rule:</strong> Your monthly housing payment (PITI: Principal, Interest, Taxes, Insurance) should not exceed 28% of your gross monthly income.
              </li>
              <li>
                <strong>36% Rule:</strong> Your total monthly debt payments (including housing) should not exceed 36% of your gross monthly income.
              </li>
              <li>
                <strong>Important:</strong> These are industry guidelines used by lenders to determine how much you can borrow. Your personal comfort level may differ.
              </li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomeAffordabilityCalculator;
