import React, { useState, useEffect } from 'react';
import { 
  convertToAnnualIncome,
  calculateProgressiveTax,
  calculateFICATax, 
  calculateStateTax, 
  calculateLocalTax, 
  calculateTotalTax,
  validateInputs 
} from '../utils/taxCalculators';
import { STATE_TAX_DATA, FEDERAL_TAX_BRACKETS, TAX_TYPES } from '../data/tax-data';
import { 
  calculateMonthlyMortgage,
  calculateMortgagePercentage,
  calculateMaxHomePrice,
  validateMortgageInputs,
  formatCurrency
} from '../utils/mortgageCalculators';
import './TaxCalculator.css';

const TaxCalculator = () => {
  const [formData, setFormData] = useState({
    income: '',
    payType: 'annual',
    state: '',
    city: '',
    monthlyDebts: '',
    // New mortgage fields
    includeHomePurchase: false,
    homePrice: '',
    downPaymentPercent: '20',
    interestRate: '6.5',
    loanTermYears: '30'
  });

  const [errors, setErrors] = useState({});
  const [results, setResults] = useState(null);
  const [showResults, setShowResults] = useState(false);
  const [localOptions, setLocalOptions] = useState([]);

  useEffect(() => {
    // If state changes, reset city and update local options
    if (formData.state) {
      const stateData = STATE_TAX_DATA[formData.state];
      if (stateData && stateData.hasLocalTax) {
        // Reset city selection
        setFormData(prev => ({ ...prev, city: '' }));
      } else {
        // Clear local options if state doesn't have local tax
        setLocalOptions([]);
      }
    } else {
      setLocalOptions([]);
    }
  }, [formData.state]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  // Validate all form inputs - combined tax and mortgage validation
  const validateFormInputs = (inputs) => {
    // First validate tax inputs
    const taxErrors = validateInputs(inputs);

    // Then validate mortgage inputs if applicable
    const mortgageErrors = validateMortgageInputs(inputs);

    // Combine both sets of errors
    return { ...taxErrors, ...mortgageErrors };
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validate inputs using combined validation
    const validationErrors = validateFormInputs(formData);
    setErrors(validationErrors);

    // If no errors, calculate taxes and mortgage (if applicable)
    if (Object.keys(validationErrors).length === 0) {
      calculateTaxes();
    }
  };

  const calculateTaxes = () => {
    const annualIncome = convertToAnnualIncome(formData.income, formData.payType);

    // Get tax calculation results
    const taxResults = calculateTotalTax(annualIncome, formData.state, formData.city);

    const takeHomePay = annualIncome - taxResults.total;
    const monthlyTakeHome = takeHomePay / 12;

    // Calculate mortgage details if enabled
    let mortgageResults = null;
    if (formData.includeHomePurchase && formData.homePrice) {
      const monthlyMortgage = calculateMonthlyMortgage(
        parseFloat(formData.homePrice),
        parseFloat(formData.downPaymentPercent),
        parseFloat(formData.interestRate),
        parseInt(formData.loanTermYears, 10)
      );

      const mortgagePercentage = calculateMortgagePercentage(monthlyMortgage, monthlyTakeHome);

      // Calculate max affordable home price
      const maxHomePrice = calculateMaxHomePrice(
        monthlyTakeHome,
        parseFloat(formData.interestRate),
        parseInt(formData.loanTermYears, 10),
        parseFloat(formData.downPaymentPercent),
        parseFloat(formData.monthlyDebts) || 0
      );

      mortgageResults = {
        monthlyMortgage,
        mortgagePercentage,
        maxHomePrice,
        isAffordable: mortgagePercentage <= 28 // 28% rule of thumb
      };
    }

    setResults({
      annualIncome,
      federalTax: taxResults.federal,
      ficaTax: taxResults.fica,
      stateTax: taxResults.state,
      localTax: taxResults.local,
      totalTax: taxResults.total,
      takeHomePay,
      effectiveRate: taxResults.effectiveRate * 100,
      monthlyTakeHome,
      monthlyDebts: parseFloat(formData.monthlyDebts) || 0,
      mortgage: mortgageResults
    });

    setShowResults(true);
  };

  // Determine what type of local jurisdiction to show based on state
  const getLocalJurisdictionLabel = () => {
    if (!formData.state || !STATE_TAX_DATA[formData.state]?.hasLocalTax) return '';

    const stateInfo = STATE_TAX_DATA[formData.state];

    switch (stateInfo.taxType) {
      case 'county':
        return 'County';
      case 'city':
        return 'City';
      case 'both':
        return 'City/County';
      case 'school_district':
        return 'School District';
      default:
        return 'Local Jurisdiction';
    }
  };

  return (
    <div className="tax-calculator">
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="income">Income:</label>
          <input
            type="text"
            id="income"
            name="income"
            value={formData.income}
            onChange={handleInputChange}
            placeholder="Enter your income"
          />
          {errors.income && <div className="error">{errors.income}</div>}
        </div>

        <div className="form-group">
          <label htmlFor="payType">Pay Type:</label>
          <select
            id="payType"
            name="payType"
            value={formData.payType}
            onChange={handleInputChange}
          >
            <option value="hourly">Hourly</option>
            <option value="weekly">Weekly</option>
            <option value="biweekly">Bi-Weekly</option>
            <option value="monthly">Monthly</option>
            <option value="annual">Annual</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="state">State:</label>
          <select
            id="state"
            name="state"
            value={formData.state}
            onChange={handleInputChange}
          >
            <option value="">Select State</option>
            {Object.keys(STATE_TAX_DATA).map(state => (
              <option key={state} value={state}>{state}</option>
            ))}
          </select>
          {errors.state && <div className="error">{errors.state}</div>}
        </div>

        {formData.state && STATE_TAX_DATA[formData.state]?.hasLocalTax && (
          <div className="form-group">
            <label htmlFor="city">{getLocalJurisdictionLabel()}:</label>
            <input
              type="text"
              id="city"
              name="city"
              value={formData.city}
              onChange={handleInputChange}
              placeholder={`Enter ${getLocalJurisdictionLabel()} name`}
            />
            {errors.city && <div className="error">{errors.city}</div>}
          </div>
        )}

        <div className="form-group">
          <label htmlFor="monthlyDebts">Monthly Debts (optional):</label>
          <input
            type="text"
            id="monthlyDebts"
            name="monthlyDebts"
            value={formData.monthlyDebts}
            onChange={handleInputChange}
            placeholder="Enter monthly debt payments"
          />
          {errors.monthlyDebts && <div className="error">{errors.monthlyDebts}</div>}
        </div>

        {/* Mortgage Analysis Toggle */}
        <div className="form-group toggle">
          <label htmlFor="includeHomePurchase">
            <input
              type="checkbox"
              id="includeHomePurchase"
              name="includeHomePurchase"
              checked={formData.includeHomePurchase}
              onChange={(e) => setFormData(prev => ({ ...prev, includeHomePurchase: e.target.checked }))}
            />
            Include Mortgage Analysis
          </label>
        </div>

        {/* Mortgage Analysis Section */}
        {formData.includeHomePurchase && (
          <div className="mortgage-section">
            <h3>Mortgage Analysis</h3>

            <div className="form-group">
              <label htmlFor="homePrice">Home Price:</label>
              <input
                type="text"
                id="homePrice"
                name="homePrice"
                value={formData.homePrice}
                onChange={handleInputChange}
                placeholder="Enter home price"
              />
              {errors.homePrice && <div className="error">{errors.homePrice}</div>}
            </div>

            <div className="form-group">
              <label htmlFor="downPaymentPercent">Down Payment (%):</label>
              <input
                type="text"
                id="downPaymentPercent"
                name="downPaymentPercent"
                value={formData.downPaymentPercent}
                onChange={handleInputChange}
                placeholder="Enter down payment percentage"
              />
              {errors.downPaymentPercent && <div className="error">{errors.downPaymentPercent}</div>}
            </div>

            <div className="form-group">
              <label htmlFor="interestRate">Interest Rate (%):</label>
              <input
                type="text"
                id="interestRate"
                name="interestRate"
                value={formData.interestRate}
                onChange={handleInputChange}
                placeholder="Enter interest rate"
              />
              {errors.interestRate && <div className="error">{errors.interestRate}</div>}
            </div>

            <div className="form-group">
              <label htmlFor="loanTermYears">Loan Term (years):</label>
              <select
                id="loanTermYears"
                name="loanTermYears"
                value={formData.loanTermYears}
                onChange={handleInputChange}
              >
                <option value="15">15 years</option>
                <option value="20">20 years</option>
                <option value="30">30 years</option>
              </select>
              {errors.loanTermYears && <div className="error">{errors.loanTermYears}</div>}
            </div>
          </div>
        )}

        <button type="submit" className="calculate-btn">Calculate Taxes</button>
      </form>

      {showResults && results && (
        <div className="results">
          <h2>Tax Calculation Results</h2>

          <div className="result-item">
            <span>Annual Income:</span>
            <span>${results.annualIncome.toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 })}</span>
          </div>

          <div className="result-item">
            <span>Federal Tax:</span>
            <span>${results.federalTax.toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 })}</span>
          </div>

          <div className="result-item">
            <span>FICA Tax:</span>
            <span>${results.ficaTax.toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 })}</span>
          </div>

          <div className="result-item">
            <span>State Tax:</span>
            <span>${results.stateTax.toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 })}</span>
          </div>

          {results.localTax > 0 && (
            <div className="result-item">
              <span>Local Tax:</span>
              <span>${results.localTax.toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 })}</span>
            </div>
          )}

          <div className="result-item total">
            <span>Total Tax:</span>
            <span>${results.totalTax.toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 })}</span>
          </div>

          <div className="result-item">
            <span>Effective Tax Rate:</span>
            <span>{results.effectiveRate.toFixed(2)}%</span>
          </div>

          <div className="result-item take-home">
            <span>Annual Take-Home Pay:</span>
            <span>${results.takeHomePay.toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 })}</span>
          </div>

          <div className="result-item take-home">
            <span>Monthly Take-Home Pay:</span>
            <span>${results.monthlyTakeHome.toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 })}</span>
          </div>

          {formData.monthlyDebts && (
            <div className="result-item">
              <span>Monthly Income After Debts:</span>
              <span>${(results.monthlyTakeHome - parseFloat(formData.monthlyDebts)).toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 })}</span>
            </div>
          )}

          {/* Mortgage Results Section */}
          {results.mortgage && (
            <div className="mortgage-results">
              <h3>Mortgage Analysis</h3>

              <div className="result-item">
                <span>Monthly Mortgage Payment:</span>
                <span>${results.mortgage.monthlyMortgage.toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 2 })}</span>
              </div>

              <div className="result-item">
                <span>Percentage of Take-Home Pay:</span>
                <span 
                  className={results.mortgage.isAffordable ? "affordable" : "unaffordable"}
                >
                  {results.mortgage.mortgagePercentage.toFixed(1)}%
                </span>
              </div>

              <div className="result-item">
                <span>Maximum Affordable Home Price:</span>
                <span>${results.mortgage.maxHomePrice.toLocaleString(undefined, { minimumFractionDigits: 0, maximumFractionDigits: 0 })}</span>
              </div>

              <div className="affordability-indicator">
                <div className={`indicator ${results.mortgage.isAffordable ? "affordable" : "unaffordable"}`}>
                  {results.mortgage.isAffordable 
                    ? "✓ This mortgage appears affordable based on your take-home pay."
                    : "⚠️ This mortgage exceeds the recommended 28% of your take-home pay."}
                </div>
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default TaxCalculator;
