  }
};

// Calculate progressive tax (like federal income tax)
const calculateProgressiveTax = (income, brackets) => {
  let tax = 0;
  let remainingIncome = income;
  let previousLimit = 0;
  for (let i = 0; i < brackets.length; i++) {
    const currentBracket = brackets[i];

    // Calculate income in this bracket
    const bracketIncome = Math.min(remainingIncome, currentBracket.limit - previousLimit);
    if (bracketIncome <= 0) break;
    tax += bracketIncome * currentBracket.rate;
    remainingIncome -= bracketIncome;
    previousLimit = currentBracket.limit;
    if (remainingIncome <= 0) break;
  }
  return tax;
};

