import React, { useState, useEffect } from 'react';
import './TaxCalculator.css';
import { INDIANA_COUNTIES } from '../data/indianaTaxData';
import { MARYLAND_COUNTIES } from '../data/marylandTaxData';
import { MICHIGAN_CITIES, calculateMichiganLocalTax } from '../data/michiganTaxData';
import { MISSOURI_CITIES } from '../data/missouriTaxData';
import { NEW_JERSEY_CITIES } from '../data/newJerseyTaxData';
import { NEW_YORK_TAX, calculateNYTax } from '../data/newYorkTaxData';
import { OHIO_MUNICIPALITIES } from '../data/ohioTaxData';
import { OREGON_TAX } from '../data/oregon-tax';
import { PENNSYLVANIA_TAX } from '../data/pennsylvaniaTaxData';
import { WEST_VIRGINIA_TAX } from '../data/westVirginiaTaxData';
import { STATE_TAX_DATA } from '../data/tax-data';

// Utility functions brought in from the imported files
// From mortgageCalculators.js
const calculateMonthlyMortgage = (homePrice, downPaymentPercent, interestRate, loanTermYears) => {
  // Calculate loan amount
  const downPayment = homePrice * (downPaymentPercent / 100);
  const loanAmount = homePrice - downPayment;

  // Convert annual interest rate to monthly and decimal form
  const monthlyInterestRate = (interestRate / 100) / 12;

  // Calculate number of payments
  const numberOfPayments = loanTermYears * 12;

  // Avoid division by zero for 0% interest
  if (monthlyInterestRate === 0) {
    return loanAmount / numberOfPayments;
  }

  // Use mortgage payment formula: M = P * (r * (1 + r)^n) / ((1 + r)^n - 1)
  const monthlyPayment = loanAmount * 
    (monthlyInterestRate * Math.pow(1 + monthlyInterestRate, numberOfPayments)) / 
    (Math.pow(1 + monthlyInterestRate, numberOfPayments) - 1);

  return monthlyPayment;
};

// Calculate maximum home price based on take-home pay
const calculateMaxHomePrice = (monthlyTakeHome, interestRate, loanTermYears, downPaymentPercent, monthlyDebts = 0) => {
  // Maximum payment should be 28% of monthly take-home pay minus existing debts
  const maxMonthlyPayment = (monthlyTakeHome * 0.28) - monthlyDebts;

  if (maxMonthlyPayment <= 0) return 0;

  // Convert annual interest rate to monthly
  const monthlyInterestRate = (interestRate / 100) / 12;

  // Number of payments
  const numberOfPayments = loanTermYears * 12;

  // Avoid division by zero for 0% interest
  if (monthlyInterestRate === 0) {
    const maxLoanAmount = maxMonthlyPayment * numberOfPayments;
    return maxLoanAmount / (1 - (downPaymentPercent / 100));
  }

  // Calculate maximum loan amount
  // Using formula: P = pmt * (1 - (1 + r)^-n) / r
  const maxLoanAmount = maxMonthlyPayment * 
    (1 - Math.pow(1 + monthlyInterestRate, -numberOfPayments)) / 
    monthlyInterestRate;

  // Calculate max home price (loan amount + down payment)
  const maxHomePrice = maxLoanAmount / (1 - (downPaymentPercent / 100));

  return maxHomePrice;
};

// Format currency for display
const formatCurrency = (value) => {
  return new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
    maximumFractionDigits: 0
  }).format(value);
};

// From taxCalculators.js
// Federal Tax Brackets 2024
const FEDERAL_TAX_BRACKETS = [
  { limit: 11600, rate: 0.10 },
  { limit: 47150, rate: 0.12 },
  { limit: 100525, rate: 0.22 },
  { limit: 191950, rate: 0.24 },
  { limit: 243725, rate: 0.32 },
  { limit: 609350, rate: 0.35 },
  { limit: Infinity, rate: 0.37 }
];

// FICA rates
const FICA_RATES = {
  socialSecurity: 0.062,  // 6.2%
  medicare: 0.0145,      // 1.45%
  additionalMedicare: 0.009, // 0.9% additional Medicare tax for high earners
  socialSecurityWageCap: 168600 // 2024 wage cap for Social Security tax
};

// State tax rates (simplified)
const STATE_TAX_RATES = {
  "Alabama": 0.05,
  "Alaska": 0.00,
  "Arizona": 0.025,
  "Arkansas": 0.039,
  "California": 0.095,
  "Colorado": 0.044,
  "Connecticut": 0.0699,
  "Delaware": 0.066,
  "Florida": 0.00,
  "Georgia": 0.0539,
  "Hawaii": 0.11,
  "Idaho": 0.059,
  "Illinois": 0.049,
  "Indiana": 0.03,
  "Iowa": 0.038,
  "Kansas": 0.055,
  "Kentucky": 0.04,
  "Louisiana": 0.03,
  "Maine": 0.071,
  "Maryland": 0.057,
  "Massachusetts": 0.09,
  "Michigan": 0.042,
  "Minnesota": 0.098,
  "Mississippi": 0.044,
  "Missouri": 0.047,
  "Montana": 0.059,
  "Nebraska": 0.052,
  "Nevada": 0.00,
  "New Hampshire": 0.05,
  "New Jersey": 0.057,
  "New Mexico": 0.059,
  "New York": 0.065,
  "North Carolina": 0.0425,
  "North Dakota": 0.025,
  "Ohio": 0.035,
  "Oklahoma": 0.0475,
  "Oregon": 0.099,
  "Pennsylvania": 0.0307,
  "Rhode Island": 0.0599,
  "South Carolina": 0.062,
  "South Dakota": 0.00,
  "Tennessee": 0.00,
  "Texas": 0.00,
  "Utah": 0.0455,
  "Vermont": 0.0875,
  "Virginia": 0.0575,
  "Washington": 0.00,
  "West Virginia": 0.0482,
  "Wisconsin": 0.0765,
  "Wyoming": 0.00
};

// Convert various income types to annual
const convertToAnnualIncome = (income, payType) => {
  const numericIncome = parseFloat(income);

  if (isNaN(numericIncome)) return 0;

  switch (payType) {
    case 'hourly':
      return numericIncome * 40 * 52; // 40 hours per week, 52 weeks per year
    case 'weekly':
      return numericIncome * 52;
    case 'biweekly':
      return numericIncome * 26;
    case 'monthly':
      return numericIncome * 12;
    case 'annual':
    default:
      return numericIncome;
  }
};

// Calculate progressive tax (like federal income tax)
const calculateProgressiveTax = (income, brackets) => {
  let tax = 0;
  let remainingIncome = income;
  let previousLimit = 0;

  for (let i = 0; i < brackets.length; i++) {
    const currentBracket = brackets[i];

    // Calculate income in this bracket
    const bracketIncome = Math.min(
      remainingIncome,
      currentBracket.limit - previousLimit
    );

    if (bracketIncome <= 0) break;

    tax += bracketIncome * currentBracket.rate;
    remainingIncome -= bracketIncome;
    previousLimit = currentBracket.limit;

    if (remainingIncome <= 0) break;
  }

  return tax;
};

// Calculate FICA taxes (Social Security and Medicare)
const calculateFICATax = (income) => {
  const { socialSecurity, medicare, additionalMedicare, socialSecurityWageCap } = FICA_RATES;

  // Social Security has a wage cap
  const socialSecurityTax = Math.min(income, socialSecurityWageCap) * socialSecurity;

  // Regular Medicare tax
  let medicareTax = income * medicare;

  // Additional Medicare tax for high earners (over $200,000)
  if (income > 200000) {
    medicareTax += (income - 200000) * additionalMedicare;
  }

  return socialSecurityTax + medicareTax;
};

// Helper function to safely get tax rate value from different tax structures
const getTaxRateValue = (taxData) => {
  if (!taxData) return 0;

  if (taxData.type === "percentage" || taxData.type === "flat") {
    return taxData.value;
  } else if (taxData.type === "range") {
    return (taxData.min + taxData.max) / 2; // Use average of range
  } else if (taxData.type === "fixed") {
    return taxData.value;
  }

  return 0;
};

// Calculate local tax (city or county)
const calculateLocalTax = (income, state, city) => {
  if (!state || !city || !STATE_TAX_DATA[state] || !STATE_TAX_DATA[state].hasLocalTax) {
    return 0;
  }

  switch (state) {
    case 'Indiana':
      if (INDIANA_COUNTIES[city]) {
        return income * getTaxRateValue(INDIANA_COUNTIES[city]);
      }
      break;
    case 'Maryland':
      if (MARYLAND_COUNTIES[city]) {
        return income * getTaxRateValue(MARYLAND_COUNTIES[city]);
      }
      break;
    case 'Michigan':
      return calculateMichiganLocalTax(income, city);
    case 'Missouri':
      if (MISSOURI_CITIES[city]) {
        return income * getTaxRateValue(MISSOURI_CITIES[city]);
      }
      break;
    case 'New Jersey':
      if (NEW_JERSEY_CITIES[city]) {
        return income * getTaxRateValue(NEW_JERSEY_CITIES[city]);
      }
      break;
    case 'New York':
      if (NEW_YORK_TAX.cities[city]) {
        const taxResult = calculateNYTax(income, city);
        return taxResult.cityTax;
      }
      break;
    case 'Ohio':
      if (OHIO_MUNICIPALITIES[city]) {
        return income * getTaxRateValue(OHIO_MUNICIPALITIES[city]);
      }
      break;
    case 'Oregon':
      return OREGON_TAX.calculateTax(income).localTax;
    case 'Pennsylvania':
      if (PENNSYLVANIA_TAX.counties[city]) {
        const countyData = PENNSYLVANIA_TAX.counties[city];
        if (countyData.type === "fixed") {
          return income * countyData.value;
        } else if (countyData.type === "range") {
          return income * ((countyData.max + countyData.min) / 2);
        }
      }
      break;
    case 'West Virginia':
      if (WEST_VIRGINIA_TAX.cities[city]) {
        return WEST_VIRGINIA_TAX.calculateTax(city);
      }
      break;
  }

  return 0;
};

// Calculate federal tax
const calculateFederalTax = (income) => {
  return calculateProgressiveTax(income, FEDERAL_TAX_BRACKETS);
};

// Calculate state tax (simplified)
const calculateStateTax = (income, state) => {
  if (!state || !STATE_TAX_RATES[state]) return 0;
  return income * STATE_TAX_RATES[state];
};

// Calculate total tax burden
const calculateTotalTax = (income, state, city) => {
  const federalTax = calculateFederalTax(income);
  const ficaTax = calculateFICATax(income);
  const stateTax = calculateStateTax(income, state);
  const localTax = calculateLocalTax(income, state, city);

  return {
    federal: federalTax,
    fica: ficaTax,
    state: stateTax,
    local: localTax,
    total: federalTax + ficaTax + stateTax + localTax,
    effectiveRate: (federalTax + ficaTax + stateTax + localTax) / income
  };
};

const HomeAffordabilityCalculator = () => {
  // Income and tax state
  const [incomeData, setIncomeData] = useState({
    income: '',
    payType: 'annual',
    state: '',
    city: '', // Added city field for local tax calculations
    useCustomTakeHome: false,
    monthlyTakeHome: ''
  });

  // Housing state
  const [housingData, setHousingData] = useState({
    homePrice: '',
    downPaymentPercent: '20',
    interestRate: '6.5',
    loanTermYears: '30',
    includePropertyTax: true,
    propertyTaxRate: '1.1',
    includeHomeInsurance: true,
    homeInsurance: '1200'
  });

  // Additional financial details
  const [financialData, setFinancialData] = useState({
    monthlyDebts: '',
    monthlyExpenses: ''
  });

  // Results and UI state
  const [errors, setErrors] = useState({});
  const [results, setResults] = useState(null);
  const [activeTab, setActiveTab] = useState('what-can-i-afford');
  const [showDetailedResults, setShowDetailedResults] = useState(false);
  const [showAdvancedOptions, setShowAdvancedOptions] = useState(false);
  const [paymentsByTerm, setPaymentsByTerm] = useState(null);

  // Get available jurisdictions (city/county) for a state
  const getJurisdictionsForState = (state) => {
    if (!state || !STATE_TAX_DATA[state] || !STATE_TAX_DATA[state].hasLocalTax) {
      return [];
    }

    switch (state) {
      case 'Indiana':
        return Object.keys(INDIANA_COUNTIES);
      case 'Maryland':
        return Object.keys(MARYLAND_COUNTIES);
      case 'Michigan':
        return [...Object.keys(MICHIGAN_CITIES.exceptions), 'Other'];
      case 'Missouri':
        return Object.keys(MISSOURI_CITIES);
      case 'New Jersey':
        return Object.keys(NEW_JERSEY_CITIES);
      case 'New York':
        return Object.keys(NEW_YORK_TAX.cities);
      case 'Ohio':
        // Limit to just a few major cities for UI simplicity
        return ['Akron', 'Cincinnati', 'Cleveland', 'Columbus', 'Dayton', 'Toledo', 'Other'];
      case 'Pennsylvania':
        // Use counties from PENNSYLVANIA_TAX, adding Philadelphia specifically
        return ['Philadelphia', ...Object.keys(PENNSYLVANIA_TAX.counties).slice(0, 10), 'Other'];
      case 'West Virginia':
        return Object.keys(WEST_VIRGINIA_TAX.cities);
      default:
        return [];
    }
  };

  // Get jurisdiction label based on state
  const getJurisdictionLabel = (state) => {
    if (!state || !STATE_TAX_DATA[state]) return 'Municipality/City';

    const stateData = STATE_TAX_DATA[state];

    switch (stateData.taxType) {
      case 'county':
        return 'County';
      case 'city':
        return 'City/Municipality';
      case 'school_district':
        return 'School District';
      case 'both':
        return 'City/County';
      default:
        return 'Municipality/City';
    }
  };

  // Handle changes to income data
  const handleIncomeChange = (field, value) => {
    setIncomeData(prev => {
      // If changing state, reset city
      if (field === 'state') {
        return { ...prev, [field]: value, city: '' };
      }
      return { ...prev, [field]: value };
    });
  };

  // Handle changes to housing data
  const handleHousingChange = (field, value) => {
    setHousingData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  // Handle changes to financial data
  const handleFinancialChange = (field, value) => {
    setFinancialData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  // Validate the input form
  const validateForm = () => {
    const newErrors = {};

    if (!incomeData.useCustomTakeHome) {
      // Validate income inputs
      if (!incomeData.income) {
        newErrors.income = 'Annual income is required';
      } else if (isNaN(parseFloat(incomeData.income)) || parseFloat(incomeData.income) <= 0) {
        newErrors.income = 'Please enter a valid income amount';
      }

      if (!incomeData.state) {
        newErrors.state = 'State selection is required for tax calculation';
      }

      // Validate city selection if state has local taxes
      if (incomeData.state && STATE_TAX_DATA[incomeData.state] && 
          STATE_TAX_DATA[incomeData.state].hasLocalTax && !incomeData.city) {
        newErrors.city = `${getJurisdictionLabel(incomeData.state)} selection is required for accurate tax calculation`;
      }
    } else {
      // Validate custom take-home pay
      if (!incomeData.monthlyTakeHome) {
        newErrors.monthlyTakeHome = 'Monthly take-home pay is required';
      } else if (isNaN(parseFloat(incomeData.monthlyTakeHome)) || parseFloat(incomeData.monthlyTakeHome) <= 0) {
        newErrors.monthlyTakeHome = 'Please enter a valid monthly take-home amount';
      }
    }

    // If we're analyzing a specific home, validate home price
    if (activeTab === 'analyze-mortgage' && !housingData.homePrice) {
      newErrors.homePrice = 'Home price is required';
    } else if (activeTab === 'analyze-mortgage' && (isNaN(parseFloat(housingData.homePrice)) || parseFloat(housingData.homePrice) <= 0)) {
      newErrors.homePrice = 'Please enter a valid home price';
    }

    // Validate mortgage-related inputs
    if (!housingData.downPaymentPercent || isNaN(parseFloat(housingData.downPaymentPercent)) || 
        parseFloat(housingData.downPaymentPercent) < 0 || parseFloat(housingData.downPaymentPercent) > 100) {
      newErrors.downPaymentPercent = 'Down payment must be between 0% and 100%';
    }

    if (!housingData.interestRate || isNaN(parseFloat(housingData.interestRate)) || 
        parseFloat(housingData.interestRate) < 0 || parseFloat(housingData.interestRate) > 30) {
      newErrors.interestRate = 'Interest rate must be between 0% and 30%';
    }

    // Validate optional financial data
    if (financialData.monthlyDebts && (isNaN(parseFloat(financialData.monthlyDebts)) || parseFloat(financialData.monthlyDebts) < 0)) {
      newErrors.monthlyDebts = 'Please enter a valid monthly debt amount';
    }

    if (financialData.monthlyExpenses && (isNaN(parseFloat(financialData.monthlyExpenses)) || parseFloat(financialData.monthlyExpenses) < 0)) {
      newErrors.monthlyExpenses = 'Please enter a valid monthly expenses amount';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Calculate affordability (what can I afford)
  const calculateAffordability = () => {
    if (!validateForm()) return;

    try {
      let annualIncome = 0;
      let monthlyTakeHome = 0;
      let taxResults = { total: 0, federal: 0, fica: 0, state: 0, local: 0, effectiveRate: 0 };

      // Calculate take-home pay
      if (!incomeData.useCustomTakeHome) {
        // Convert to annual income and calculate taxes
        annualIncome = convertToAnnualIncome(incomeData.income, incomeData.payType);
        taxResults = calculateTotalTax(annualIncome, incomeData.state, incomeData.city);
        const takeHomePay = annualIncome - taxResults.total;
        monthlyTakeHome = takeHomePay / 12;
      } else {
        // Use custom take-home pay
        monthlyTakeHome = parseFloat(incomeData.monthlyTakeHome);
        annualIncome = monthlyTakeHome * 12; // Approximate
      }

      // Parse other inputs
      const monthlyDebts = parseFloat(financialData.monthlyDebts) || 0;
      const monthlyExpenses = parseFloat(financialData.monthlyExpenses) || 0;
      const interestRate = parseFloat(housingData.interestRate);
      const loanTermYears = parseInt(housingData.loanTermYears, 10);
      const downPaymentPercent = parseFloat(housingData.downPaymentPercent);

      // Calculate monthly property tax and insurance if included
      let additionalHousingCosts = 0;

      // Calculate affordability ratios
      // Front-end ratio: 28% of monthly take-home for housing (PITI)
      const frontEndMaxPayment = monthlyTakeHome * 0.28;
      // Back-end ratio: 36% of monthly take-home for all debt payments
      const backEndMaxPayment = (monthlyTakeHome * 0.36) - monthlyDebts;
      // Use the more conservative of the two
      const maxMonthlyPayment = Math.min(frontEndMaxPayment, backEndMaxPayment);

      // Adjust for property tax and insurance (will be calculated more precisely later)
      if (housingData.includePropertyTax) {
        const propertyTaxRate = parseFloat(housingData.propertyTaxRate) / 100; // Convert from percent to decimal
        // Will estimate with a placeholder value for now
        additionalHousingCosts += (300000 * propertyTaxRate) / 12; // Placeholder home value for estimation
      }

      if (housingData.includeHomeInsurance) {
        const annualInsurance = parseFloat(housingData.homeInsurance) || 1200;
        additionalHousingCosts += annualInsurance / 12;
      }

      // Adjust max payment to account for property tax and insurance
      const maxPIPayment = maxMonthlyPayment - additionalHousingCosts;

      if (maxPIPayment <= 0) {
        setErrors({ 
          general: 'Your expenses and debts are too high relative to your income for a mortgage'
        });
        return;
      }

      // Calculate max home price
      const maxHomePrice = calculateMaxHomePrice(
        monthlyTakeHome,
        interestRate,
        loanTermYears,
        downPaymentPercent,
        monthlyDebts + monthlyExpenses
      );

      const maxLoanAmount = maxHomePrice * (1 - (downPaymentPercent / 100));
      const downPaymentAmount = maxHomePrice * (downPaymentPercent / 100);

      // Calculate monthly mortgage payment
      const baseMonthlyPayment = calculateMonthlyMortgage(
        maxHomePrice,
        downPaymentPercent,
        interestRate,
        loanTermYears
      );

      // Calculate payment with taxes and insurance more precisely now that we know max home price
      let totalMonthlyPayment = baseMonthlyPayment;
      let monthlyPropertyTax = 0;
      let monthlyInsurance = 0;

      if (housingData.includePropertyTax) {
        const propertyTaxRate = parseFloat(housingData.propertyTaxRate) / 100;
        monthlyPropertyTax = (maxHomePrice * propertyTaxRate) / 12;
        totalMonthlyPayment += monthlyPropertyTax;
      }

      if (housingData.includeHomeInsurance) {
        const annualInsurance = parseFloat(housingData.homeInsurance) || 1200;
        monthlyInsurance = annualInsurance / 12;
        totalMonthlyPayment += monthlyInsurance;
      }

      // Calculate payment options for different terms
      const termOptions = [15, 20, 30];
      const paymentOptions = {};

      termOptions.forEach(term => {
        const termPayment = calculateMonthlyMortgage(
          maxHomePrice,
          downPaymentPercent,
          interestRate,
          term
        );

        let totalTermPayment = termPayment;

        if (housingData.includePropertyTax) {
          totalTermPayment += monthlyPropertyTax;
        }

        if (housingData.includeHomeInsurance) {
          totalTermPayment += monthlyInsurance;
        }

        const totalInterest = (termPayment * (term * 12)) - maxLoanAmount;

        paymentOptions[term] = {
          principalAndInterest: termPayment,
          totalPayment: totalTermPayment,
          totalInterest: totalInterest,
          percentOfTakeHome: (totalTermPayment / monthlyTakeHome) * 100,
          isAffordable: (totalTermPayment / monthlyTakeHome) <= 0.28
        };
      });

      setPaymentsByTerm(paymentOptions);

      // Create results object
      const affordabilityResults = {
        // Income details
        annualIncome,
        monthlyTakeHome,

        // Tax details (if calculated)
        federalTax: taxResults.federal,
        ficaTax: taxResults.fica,
        stateTax: taxResults.state,
        localTax: taxResults.local,
        totalTax: taxResults.total,
        effectiveTaxRate: taxResults.effectiveRate * 100,

        // Financial details
        monthlyDebts,
        monthlyExpenses,
        totalMonthlyObligations: monthlyDebts + monthlyExpenses,

        // Affordability details
        frontEndMaxPayment,
        backEndMaxPayment,
        maxMonthlyPIPayment: maxPIPayment,
        maxMonthlyAllPayment: maxMonthlyPayment,

        // Mortgage details
        maxHomePrice,
        maxLoanAmount,
        downPaymentAmount,
        downPaymentPercent,
        baseMonthlyPayment,
        monthlyPropertyTax,
        monthlyInsurance,
        totalMonthlyPayment,

        // Percentages
        percentOfTakeHomePay: (totalMonthlyPayment / monthlyTakeHome) * 100,
        isAffordable: (totalMonthlyPayment / monthlyTakeHome) <= 0.28
      };

      setResults(affordabilityResults);
    } catch (error) {
      console.error("Calculation error:", error);
      setErrors({ general: "An error occurred during calculation. Please check your inputs." });
    }
  };

  // Analyze a specific home price
  const analyzeSpecificHome = () => {
    if (!validateForm()) return;

    try {
      let annualIncome = 0;
      let monthlyTakeHome = 0;
      let taxResults = { total: 0, federal: 0, fica: 0, state: 0, local: 0, effectiveRate: 0 };

      // Calculate take-home pay
      if (!incomeData.useCustomTakeHome) {
        // Convert to annual income and calculate taxes
        annualIncome = convertToAnnualIncome(incomeData.income, incomeData.payType);
        taxResults = calculateTotalTax(annualIncome, incomeData.state, incomeData.city);
        const takeHomePay = annualIncome - taxResults.total;
        monthlyTakeHome = takeHomePay / 12;
      } else {
        // Use custom take-home pay
        monthlyTakeHome = parseFloat(incomeData.monthlyTakeHome);
        annualIncome = monthlyTakeHome * 12; // Approximate
      }

      // Parse other inputs
      const monthlyDebts = parseFloat(financialData.monthlyDebts) || 0;
      const monthlyExpenses = parseFloat(financialData.monthlyExpenses) || 0;
      const interestRate = parseFloat(housingData.interestRate);
      const loanTermYears = parseInt(housingData.loanTermYears, 10);
      const downPaymentPercent = parseFloat(housingData.downPaymentPercent);
      const homePrice = parseFloat(housingData.homePrice);

      // Calculate loan amount and down payment
      const downPaymentAmount = homePrice * (downPaymentPercent / 100);
      const loanAmount = homePrice - downPaymentAmount;

      // Calculate monthly mortgage payment
      const baseMonthlyPayment = calculateMonthlyMortgage(
        homePrice,
        downPaymentPercent,
        interestRate,
        loanTermYears
      );

      // Calculate total monthly payment with taxes and insurance
      let totalMonthlyPayment = baseMonthlyPayment;
      let monthlyPropertyTax = 0;
      let monthlyInsurance = 0;

      if (housingData.includePropertyTax) {
        const propertyTaxRate = parseFloat(housingData.propertyTaxRate) / 100;
        monthlyPropertyTax = (homePrice * propertyTaxRate) / 12;
        totalMonthlyPayment += monthlyPropertyTax;
      }

      if (housingData.includeHomeInsurance) {
        const annualInsurance = parseFloat(housingData.homeInsurance) || 1200;
        monthlyInsurance = annualInsurance / 12;
        totalMonthlyPayment += monthlyInsurance;
      }

      // Calculate payment options for different terms
      const termOptions = [15, 20, 30];
      const paymentOptions = {};

      termOptions.forEach(term => {
        const termPayment = calculateMonthlyMortgage(
          homePrice,
          downPaymentPercent,
          interestRate,
          term
        );

        let totalTermPayment = termPayment;

        if (housingData.includePropertyTax) {
          totalTermPayment += monthlyPropertyTax;
        }

        if (housingData.includeHomeInsurance) {
          totalTermPayment += monthlyInsurance;
        }

        const totalInterest = (termPayment * (term * 12)) - loanAmount;

        paymentOptions[term] = {
          principalAndInterest: termPayment,
          totalPayment: totalTermPayment,
          totalInterest: totalInterest,
          percentOfTakeHome: (totalTermPayment / monthlyTakeHome) * 100,
          isAffordable: (totalTermPayment / monthlyTakeHome) <= 0.28
        };
      });

      setPaymentsByTerm(paymentOptions);

      // Create results object
      const affordabilityResults = {
        // Income details
        annualIncome,
        monthlyTakeHome,

        // Tax details (if calculated)
        federalTax: taxResults.federal,
        ficaTax: taxResults.fica,
        stateTax: taxResults.state,
        localTax: taxResults.local,
        totalTax: taxResults.total,
        effectiveTaxRate: taxResults.effectiveRate * 100,

        // Financial details
        monthlyDebts,
        monthlyExpenses,
        totalMonthlyObligations: monthlyDebts + monthlyExpenses,

        // Mortgage details
        homePrice,
        loanAmount,
        downPaymentAmount,
        downPaymentPercent,
        baseMonthlyPayment,
        monthlyPropertyTax,
        monthlyInsurance,
        totalMonthlyPayment,

        // Affordability metrics
        percentOfTakeHomePay: (totalMonthlyPayment / monthlyTakeHome) * 100,
        isAffordable: (totalMonthlyPayment / monthlyTakeHome) <= 0.28
      };

      setResults(affordabilityResults);
    } catch (error) {
      console.error("Calculation error:", error);
      setErrors({ general: "An error occurred during calculation. Please check your inputs." });
    }
  };

  const handleCalculate = () => {
    if (activeTab === 'what-can-i-afford') {
      calculateAffordability();
    } else {
      analyzeSpecificHome();
    }
  };

  // Get the affordability color class based on percentage
  const getAffordabilityColorClass = (percentage) => {
    if (percentage <= 25) return "green";
    if (percentage <= 28) return "green";
    if (percentage <= 32) return "yellow";
    if (percentage <= 36) return "yellow";
    return "red";
  };

  return (
    <div className="tax-calculator">
      <h2>Home Affordability Calculator</h2>
      <p>Find your ideal home price based on your take-home pay and financial situation</p>

      {/* Tabs for calculator modes */}
      <div className="calculator-tabs">
        <button 
          className={`tab-button ${activeTab === 'what-can-i-afford' ? 'active' : ''}`}
          onClick={() => setActiveTab('what-can-i-afford')}
        >
          What Can I Afford?
        </button>
        <button 
          className={`tab-button ${activeTab === 'analyze-mortgage' ? 'active' : ''}`}
          onClick={() => setActiveTab('analyze-mortgage')}
        >
          Analyze a Home Price
        </button>
      </div>

      {errors.general && (
        <div className="error-message">
          <p>{errors.general}</p>
        </div>
      )}

      <div className="calculator-grid">
        {/* Left Column - Input Form */}
        <div className="calculator-inputs">
          {/* Income Section */}
          <div className="form-section">
            <h3>Income Information</h3>

            <div className="form-group toggle">
              <input
                type="checkbox"
                id="useCustomTakeHome"
                checked={incomeData.useCustomTakeHome}
                onChange={(e) => handleIncomeChange('useCustomTakeHome', e.target.checked)}
              />
              <label htmlFor="useCustomTakeHome">
                I know my monthly take-home pay (after taxes)
              </label>
            </div>

            {!incomeData.useCustomTakeHome ? (
              <>
                <div className="form-group">
                  <label htmlFor="income">Income</label>
                  <div className="input-group">
                    <input
                      id="income"
                      value={incomeData.income}
                      onChange={(e) => handleIncomeChange('income', e.target.value)}
                      type="text"
                      placeholder="e.g. 75000"
                      className={errors.income ? "error-input" : ""}
                    />
                    <select
                      value={incomeData.payType}
                      onChange={(e) => handleIncomeChange('payType', e.target.value)}
                    >
                      <option value="hourly">Hourly</option>
                      <option value="weekly">Weekly</option>
                      <option value="biweekly">Bi-Weekly</option>
                      <option value="monthly">Monthly</option>
                      <option value="annual">Annual</option>
                    </select>
                  </div>
                  {errors.income && <p className="error">{errors.income}</p>}
                </div>

                <div className="form-group">
                  <label htmlFor="state">State</label>
                  <select
                    id="state"
                    value={incomeData.state}
                    onChange={(e) => handleIncomeChange('state', e.target.value)}
                    className={errors.state ? "error-input" : ""}
                  >
                    <option value="">Select state</option>
                    {Object.keys(STATE_TAX_RATES).sort().map(state => (
                      <option key={state} value={state}>{state}</option>
                    ))}
                  </select>
                  {errors.state && <p className="error">{errors.state}</p>}
                </div>

                {/* City/County selection dropdown - only shown if state has local taxes */}
                {incomeData.state && STATE_TAX_DATA[incomeData.state] && 
                 STATE_TAX_DATA[incomeData.state].hasLocalTax && (
                  <div className="form-group">
                    <label htmlFor="city">{getJurisdictionLabel(incomeData.state)}</label>
                    <select
                      id="city"
                      value={incomeData.city}
                      onChange={(e) => handleIncomeChange('city', e.target.value)}
                      className={errors.city ? "error-input" : ""}
                    >
                      <option value="">Select {getJurisdictionLabel(incomeData.state).toLowerCase()}</option>
                      {getJurisdictionsForState(incomeData.state).map(jurisdiction => (
                        <option key={jurisdiction} value={jurisdiction}>{jurisdiction}</option>
                      ))}
                    </select>
                    {errors.city && <p className="error">{errors.city}</p>}

                    {/* Display helpful info based on state */}
                    {incomeData.state === 'Pennsylvania' && 
                      <small>Philadelphia has a 3.75% wage tax for residents. Other municipalities typically have 1% local tax.</small>
                    }
                  </div>
                )}
              </>
            ) : (
              <div className="form-group">
                <label htmlFor="monthlyTakeHome">Monthly Take-Home Pay</label>
                <input
                  id="monthlyTakeHome"
                  value={incomeData.monthlyTakeHome}
                  onChange={(e) => handleIncomeChange('monthlyTakeHome', e.target.value)}
                  type="text"
                  placeholder="e.g. 4500"
                  className={errors.monthlyTakeHome ? "error-input" : ""}
                />
                {errors.monthlyTakeHome && <p className="error">{errors.monthlyTakeHome}</p>}
                <small>Enter your monthly income after taxes and deductions</small>
              </div>
            )}
          </div>

          {/* Other Financial Details - Keep them visible but simpler */}
          <div className="form-section">
            <h3>Financial Details</h3>

            <div className="form-group">
              <label htmlFor="monthlyDebts">Monthly Debt Payments ($)</label>
              <input
                id="monthlyDebts"
                value={financialData.monthlyDebts}
                onChange={(e) => handleFinancialChange('monthlyDebts', e.target.value)}
                type="text"
                placeholder="e.g. 500"
                className={errors.monthlyDebts ? "error-input" : ""}
              />
              {errors.monthlyDebts && <p className="error">{errors.monthlyDebts}</p>}
              <small>Car loans, student loans, credit cards, etc.</small>
            </div>
          </div>

          {/* Housing Details */}
          <div className="form-section">
            <div className="section-header">
              <h3>Mortgage Details</h3>
              <button 
                onClick={() => setShowAdvancedOptions(!showAdvancedOptions)}
                className="toggle-button"
              >
                {showAdvancedOptions ? "Hide Advanced" : "Show Advanced"}
              </button>
            </div>

            {activeTab === 'analyze-mortgage' && (
              <div className="form-group">
                <label htmlFor="homePrice">Home Price</label>
                <input
                  id="homePrice"
                  value={housingData.homePrice}
                  onChange={(e) => handleHousingChange('homePrice', e.target.value)}
                  type="text"
                  placeholder="e.g. 350000"
                  className={errors.homePrice ? "error-input" : ""}
                />
                {errors.homePrice && <p className="error">{errors.homePrice}</p>}
              </div>
            )}

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="downPaymentPercent">Down Payment (%)</label>
                <input
                  id="downPaymentPercent"
                  value={housingData.downPaymentPercent}
                  onChange={(e) => handleHousingChange('downPaymentPercent', e.target.value)}
                  type="text"
                  placeholder="e.g. 20"
                  className={errors.downPaymentPercent ? "error-input" : ""}
                />
                {errors.downPaymentPercent && (
                  <p className="error">{errors.downPaymentPercent}</p>
                )}
              </div>

              <div className="form-group">
                <label htmlFor="interestRate">Interest Rate (%)</label>
                <input
                  id="interestRate"
                  value={housingData.interestRate}
                  onChange={(e) => handleHousingChange('interestRate', e.target.value)}
                  type="text"
                  placeholder="e.g. 6.5"
                  className={errors.interestRate ? "error-input" : ""}
                />
                {errors.interestRate && <p className="error">{errors.interestRate}</p>}
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="loanTermYears">Loan Term</label>
              <select
                id="loanTermYears"
                value={housingData.loanTermYears}
                onChange={(e) => handleHousingChange('loanTermYears', e.target.value)}
              >
                <option value="15">15 years</option>
                <option value="20">20 years</option>
                <option value="30">30 years</option>
              </select>
            </div>

            {showAdvancedOptions && (
              <div className="advanced-options">
                <div className="form-group toggle">
                  <input
                    type="checkbox"
                    id="includePropertyTax"
                    checked={housingData.includePropertyTax}
                    onChange={(e) => handleHousingChange('includePropertyTax', e.target.checked)}
                  />
                  <label htmlFor="includePropertyTax">
                    Include property tax
                  </label>
                </div>

                {housingData.includePropertyTax && (
                  <div className="form-group indented">
                    <label htmlFor="propertyTaxRate">Property Tax Rate (%)</label>
                    <input
                      id="propertyTaxRate"
                      value={housingData.propertyTaxRate}
                      onChange={(e) => handleHousingChange('propertyTaxRate', e.target.value)}
                      type="text"
                      placeholder="e.g. 1.1"
                    />
                    <small>The average property tax rate in the US is 1.1%</small>
                  </div>
                )}

                <div className="form-group toggle">
                  <input
                    type="checkbox"
                    id="includeHomeInsurance"
                    checked={housingData.includeHomeInsurance}
                    onChange={(e) => handleHousingChange('includeHomeInsurance', e.target.checked)}
                  />
                  <label htmlFor="includeHomeInsurance">
                    Include home insurance
                  </label>
                </div>

                {housingData.includeHomeInsurance && (
                  <div className="form-group indented">
                    <label htmlFor="homeInsurance">Annual Insurance ($)</label>
                    <input
                      id="homeInsurance"
                      value={housingData.homeInsurance}
                      onChange={(e) => handleHousingChange('homeInsurance', e.target.value)}
                      type="text"
                      placeholder="e.g. 1200"
                    />
                    <small>The average annual home insurance cost is $1,200</small>
                  </div>
                )}
              </div>
            )}
          </div>

          <button 
            onClick={handleCalculate}
            className="calculate-btn"
          >
            {activeTab === 'what-can-i-afford' ? 'Calculate What I Can Afford' : 'Analyze This Home'}
          </button>
        </div>

        {/* Right Column - Results */}
        <div className="calculator-results">
          {results ? (
            <div className="results">
              <h2>Results Summary</h2>

              {/* Visual representation of mortgage as % of take-home pay */}
              <div className="affordability-visual">
                <h3>Mortgage Impact on Monthly Budget</h3>
                <div className="affordability-meter">
                  <div className="meter-header">
                    <span>Monthly Take-Home: {formatCurrency(results.monthlyTakeHome)}</span>
                    <span className={getAffordabilityColorClass(results.percentOfTakeHomePay)}>
                      {results.percentOfTakeHomePay.toFixed(1)}% of Income
                    </span>
                  </div>
                  <div className="meter-bar">
                    <div 
                      className={`meter-fill ${getAffordabilityColorClass(results.percentOfTakeHomePay)}`} 
                      style={{width: `${Math.min(results.percentOfTakeHomePay * 2, 100)}%`}}
                    ></div>
                  </div>
                  <div className="meter-labels">
                    <span>0%</span>
                    <span className="green">25%</span>
                    <span className="yellow">35%</span>
                    <span>50%</span>
                  </div>
                </div>
              </div>

              {/* Affordability Summary */}
              <div className="result-summary">
                {activeTab === 'what-can-i-afford' ? (
                  <>
                    <div className="result-item total">
                      <span>Maximum Home Price:</span>
                      <span className="highlight">
                        {formatCurrency(results.maxHomePrice)}
                      </span>
                    </div>

                    <div className="result-row">
                      <div className="result-item">
                        <span>Down Payment ({results.downPaymentPercent}%):</span>
                        <span>
                          {formatCurrency(results.downPaymentAmount)}
                        </span>
                      </div>

                      <div className="result-item">
                        <span>Loan Amount:</span>
                        <span>
                          {formatCurrency(results.maxLoanAmount)}
                        </span>
                      </div>
                    </div>
                  </>
                ) : (
                  <>
                    <div className={`result-item total ${results.isAffordable ? "affordable" : "unaffordable"}`}>
                      <span>Affordability:</span>
                      <span>
                        {results.isAffordable 
                          ? "✓ Affordable" 
                          : "⚠️ May be too expensive"}
                      </span>
                    </div>

                    <div className="result-row">
                      <div className="result-item">
                        <span>Home Price:</span>
                        <span>
                          {formatCurrency(results.homePrice)}
                        </span>
                      </div>

                      <div className="result-item">
                        <span>Loan Amount:</span>
                        <span>
                          {formatCurrency(results.loanAmount)}
                        </span>
                      </div>
                    </div>
                  </>
                )}
              </div>

              {/* Monthly Payment Breakdown */}
              <div className="payment-breakdown">
                <h3>Monthly Payment Breakdown</h3>

                <div className="payment-details">
                  <div className="result-item">
                    <span>Principal & Interest:</span>
                    <span>
                      {formatCurrency(results.baseMonthlyPayment)}
                    </span>
                  </div>

                  {results.monthlyPropertyTax > 0 && (
                    <div className="result-item">
                      <span>Property Tax:</span>
                      <span>
                        {formatCurrency(results.monthlyPropertyTax)}
                      </span>
                    </div>
                  )}

                  {results.monthlyInsurance > 0 && (
                    <div className="result-item">
                      <span>Home Insurance:</span>
                      <span>
                        {formatCurrency(results.monthlyInsurance)}
                      </span>
                    </div>
                  )}

                  <div className="result-item total">
                    <span>Total Monthly Payment:</span>
                    <span>
                      {formatCurrency(results.totalMonthlyPayment)}
                    </span>
                  </div>
                </div>
              </div>

              {/* Term comparison */}
              {paymentsByTerm && (
                <div className="term-comparison">
                  <h3>Payment Options by Term</h3>

                  <div className="term-options">
                    {Object.entries(paymentsByTerm).map(([term, data]) => (
                      <div key={term} className="term-option">
                        <div className="term-header">
                          <span>{term} Year Mortgage:</span>
                          <span className="term-payment">{formatCurrency(data.totalPayment)}/mo</span>
                        </div>

                        <div className="term-details">
                          <div className="term-detail">
                            <span>Total Interest:</span>
                            <span>{formatCurrency(data.totalInterest)}</span>
                          </div>

                          <div className="term-detail">
                            <span>% of Take-Home:</span>
                            <span className={getAffordabilityColorClass(data.percentOfTakeHome)}>
                              {data.percentOfTakeHome.toFixed(1)}%
                            </span>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {/* Income and Expense Details Expandable Section */}
              <div className="financial-details">
                <button
                  onClick={() => setShowDetailedResults(!showDetailedResults)}
                  className="toggle-details"
                >
                  {showDetailedResults ? "Hide Financial Details" : "Show Financial Details"}
                </button>

                {showDetailedResults && (
                  <div className="detailed-results">
                    {/* Tax Breakdown (if calculated from income) */}
                    {!incomeData.useCustomTakeHome && (
                      <div className="tax-summary">
                        <h3>Tax Summary</h3>
                        <div className="tax-details">
                          <div className="result-item">
                            <span>Annual Income:</span>
                            <span>
                              {formatCurrency(results.annualIncome)}
                            </span>
                          </div>

                          <div className="result-item">
                            <span>Federal Tax:</span>
                            <span>
                              {formatCurrency(results.federalTax)}
                            </span>
                          </div>

                          <div className="result-item">
                            <span>FICA Tax:</span>
                            <span>
                              {formatCurrency(results.ficaTax)}
                            </span>
                          </div>

                          <div className="result-item">
                            <span>State Tax:</span>
                            <span>
                              {formatCurrency(results.stateTax)}
                            </span>
                          </div>

                          {results.localTax > 0 && (
                            <div className="result-item">
                              <span>Local Tax:</span>
                              <span>
                                {formatCurrency(results.localTax)}
                              </span>
                            </div>
                          )}

                          <div className="result-item total">
                            <span>Total Tax:</span>
                            <span>
                              {formatCurrency(results.totalTax)}
                            </span>
                          </div>

                          <div className="result-item">
                            <span>Effective Tax Rate:</span>
                            <span>
                              {results.effectiveTaxRate.toFixed(1)}%
                            </span>
                          </div>
                        </div>
                      </div>
                    )}

                    {/* Monthly Budget Breakdown */}
                    <div className="budget-summary">
                      <h3>Monthly Budget</h3>
                      <div className="budget-details">
                        <div className="result-item total take-home">
                          <span>Take-Home Pay:</span>
                          <span>
                            {formatCurrency(results.monthlyTakeHome)}
                          </span>
                        </div>

                        <div className="result-item">
                          <span>Mortgage Payment:</span>
                          <span>
                            {formatCurrency(results.totalMonthlyPayment)}
                          </span>
                        </div>

                        {results.monthlyDebts > 0 && (
                          <div className="result-item">
                            <span>Other Debt Payments:</span>
                            <span>
                              {formatCurrency(results.monthlyDebts)}
                            </span>
                          </div>
                        )}

                        <div className="result-item total">
                          <span>Income Remaining:</span>
                          <span>
                            {formatCurrency(results.monthlyTakeHome - results.totalMonthlyPayment - results.monthlyDebts)}
                          </span>
                        </div>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </div>
          ) : (
            <div className="no-results">
              <h3>Complete the form and calculate</h3>
              <p>
                Fill in your income and basic mortgage details to see what home price you can afford or 
                analyze a specific home price.
              </p>
            </div>
          )}

          {/* Affordability Guidelines */}
          <div className="guidelines">
            <h3>Affordability Guidelines</h3>
            <ul>
              <li>
                <strong>28% Rule:</strong> Your monthly housing payment should not exceed 28% of your monthly take-home pay for comfortable affordability.
              </li>
              <li>
                <strong>36% Rule:</strong> Your total monthly debt payments (including housing) should ideally stay below 36% of your monthly take-home pay.
              </li>
              <li>
                <strong>PITI:</strong> Remember that your total housing costs include Principal, Interest, Taxes, and Insurance.
              </li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomeAffordabilityCalculator;
